"use strict";(self.webpackChunksfit_delivery_knowledge_hub=self.webpackChunksfit_delivery_knowledge_hub||[]).push([[2008],{7205:e=>{e.exports=JSON.parse('{"label":"Base","permalink":"/SFITDelivery-KnowledgeHub/docs/tags/base","allTagsPath":"/SFITDelivery-KnowledgeHub/docs/tags","count":2,"items":[{"id":"Management/02_04_Delivery_Strategies/02_04_01_Fundamentals_of_Delivery_Strategies","title":"Fundamentals of Delivery Strategies","description":"In modern change management, the key task is to minimize risks when deploying new features while ensuring system stability. Deployment methods such as Big Bang, Phased Rollouts, Dark Launches, Blue-Green Deployment, and A/B Testing allow for a flexible approach to implementing changes depending on the context and business goals.","permalink":"/SFITDelivery-KnowledgeHub/docs/Management/02_04_Delivery_Strategies/02_04_01_Fundamentals_of_Delivery_Strategies"},{"id":"Management/02_03_Salesforce_Deployment/02_03_01_Fundamentals_of_Deployment","title":"Fundamentals of Deployment in Salesforce","description":"Deployment is an essential stage in the Software Development Lifecycle (SDLC), ensuring the delivery of a finished product to users. This process includes code transfer and environment setup and can be performed manually or automatically. A well-structured deployment process helps avoid errors, minimize implementation time, and ensure system stability.","permalink":"/SFITDelivery-KnowledgeHub/docs/Management/02_03_Salesforce_Deployment/02_03_01_Fundamentals_of_Deployment"}],"unlisted":false}')}}]);