"use strict";(self.webpackChunksfit_delivery_knowledge_hub=self.webpackChunksfit_delivery_knowledge_hub||[]).push([[2932],{2860:e=>{e.exports=JSON.parse('{"label":"Relese","permalink":"/SFITDelivery-KnowledgeHub/docs/tags/relese","allTagsPath":"/SFITDelivery-KnowledgeHub/docs/tags","count":3,"items":[{"id":"Management/02_01_Release/02_01_07_Batch_Size","title":"Batch Size","description":"In modern change management, whether delivering new features or updates, the concept of Batch Size plays a crucial role. Batch Size is one of the key factors affecting the efficiency of the development and delivery process. It is essential to understand that Batch Size influences not only the speed of change delivery but also system stability and the overall costs incurred by a company.","permalink":"/SFITDelivery-KnowledgeHub/docs/Management/02_01_Release/02_01_07_Batch_Size"},{"id":"Management/02_01_Release/02_01_04_HotFix","title":"HotFix","description":"What is a Hotfix?","permalink":"/SFITDelivery-KnowledgeHub/docs/Management/02_01_Release/02_01_04_HotFix"},{"id":"Management/02_01_Release/02_01_06_Incremental_Releases","title":"Incremental Releases","description":"An incremental release is a software development and deployment strategy in which updates and improvements are delivered in small, manageable increments rather than large, monolithic releases. This approach increases flexibility, minimizes risks, and speeds up user feedback. An incremental release is particularly well-suited for agile development methodologies like Scrum or Kanban, where projects progress through successive releases that add new features or improvements.","permalink":"/SFITDelivery-KnowledgeHub/docs/Management/02_01_Release/02_01_06_Incremental_Releases"}],"unlisted":false}')}}]);